import { io, Socket } from 'socket.io-client';
import { useNavigate } from 'react-router-dom';


class SocketService {
  private static instance: SocketService;
  socket: Socket | null = null;

  public static getInstance(): SocketService {
    if (!SocketService.instance) {
      SocketService.instance = new SocketService();
    }
    return SocketService.instance;
  }

  initializeSocket(token: string): Promise<Socket> {
    return new Promise((resolve, reject) => {
      if (this.socket?.connected) return resolve(this.socket);

      const cleanToken = token.replace('Bearer ', '');
      const userId = localStorage.getItem('userId');

      if (!userId) {
        return reject(new Error('Missing userId in localStorage'));
      }

      if (!this.isTokenValid(cleanToken)) {
        return reject(new Error('Invalid or expired token'));
      }

      this.socket = io('http://localhost:8080', {
        path: '/socket.io',
        auth: { token: cleanToken },
        transports: ['websocket'],
        reconnectionAttempts: 5,
        reconnectionDelay: 2000,
        query: { userId, token: cleanToken },
        withCredentials: true,
      });

      this.socket.on('connect', () => {
        console.log("WebSocket connected");
        resolve(this.socket!);
      });

      this.socket.on('disconnect', (reason) => {
        console.log(`Disconnected: ${reason}`);
        if (reason === 'io server disconnect') {
          console.log('Server disconnected the client. Retrying...');
          setTimeout(() => this.initializeSocket(token), 5000); // Retry after 5 seconds
        } else if (reason === 'io client disconnect') {
          console.log('Client disconnected intentionally.');
        } else {
          console.log('Disconnected due to network issues. Retrying...');
          setTimeout(() => this.initializeSocket(token), 5000); // Retry after 5 seconds
        }
      });

      this.socket.on('connect_error', (err) => {
        console.error('Connection error:', err.message);
        if (err.message === 'Unauthorized') {
          localStorage.removeItem('token');
          const navigate = useNavigate();
          navigate('/login');
        }
        reject(err);
      });
    });
  }

  onConnectionStatusChange(callback: (status: 'connected' | 'disconnected' | 'error') => void) {
    if (!this.socket) return;

    this.socket.on('connect', () => callback('connected'));
    this.socket.on('disconnect', () => callback('disconnected'));
    this.socket.on('connect_error', () => callback('error'));
    this.socket.on('reconnect_failed', () => callback('error'));
  }

  disconnect() {
    if (this.socket) {
      console.log("üîå Disconnecting WebSocket...");
      console.trace("WebSocket disconnect called");
      this.socket.disconnect();
      this.socket = null;
      console.log("üîå WebSocket disconnected");
    }
  }

  public isTokenValid(token: string): boolean {
    try {
      const payload = JSON.parse(atob(token.split('.')[1]));
      return payload.exp * 1000 > Date.now(); // Check if token is expired
    } catch {
      return false;
    }
  }
}

export const socketService = SocketService.getInstance();

