import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import { loginResponse } from 'src/types';
import './Login.css';
import { io } from 'socket.io-client';


const Login: React.FC = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const history = useHistory();
  
    const handleLogin = async (e: React.FormEvent) => {
      e.preventDefault();
      try {
        // 1. First perform the login request
        const response = await fetch('http://localhost:8080/api/v1/auth/login', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ email, password }),
        });
  
        if (!response.ok) {
          throw new Error('Login failed');
        }
  
        const data: loginResponse = await response.json();
        
        // Add detailed debug logs
        console.log('Login successful, token received:', {
          tokenExists: !!data.accessToken,
          tokenLength: data.accessToken?.length,
          userId: data.responsePayload.id,
          fullname: data.responsePayload.fullname
        });
        
        // 2. Store the auth data
        localStorage.setItem('token', data.accessToken);
        localStorage.setItem('userId', data.responsePayload.id);
        localStorage.setItem('username', data.responsePayload.fullname);
  
        // 3. Initialize Socket.IO connection
        const socketToken = `Bearer ${data.accessToken}`;
        console.log('Attempting socket connection with token:', socketToken.substring(0, 20) + '...');
        
        const socket = io('http://localhost:8080/chat', {
          path: '/socket.io',
          auth: {
            token: socketToken
          },
          transports: ['websocket', 'polling'],
          reconnectionAttempts: 5,
          reconnectionDelay: 1000,
          withCredentials: true
        });
  
        // 4. Wait for connection before navigating
        await new Promise<void>((resolve, reject) => {
          socket.on('connect', () => {
            console.log('Socket connected successfully!');
            resolve();
          });

          socket.on('connect_error', (err) => {
            console.error('Connection error details:', {
              message: err.message,
              type: err.type,
              description: err.description,
              context: err
            });
            reject(new Error('Socket connection failed: ' + err.message));
          });

          socket.on('authentication_error', (err) => {
            console.error('Authentication error details:', err);
            reject(new Error('Authentication failed: ' + JSON.stringify(err)));
          });

          socket.on('error', (err) => {
            console.error('Socket error details:', err);
            reject(new Error('Socket error: ' + err.message));
          });

          // Timeout after 5 seconds
          setTimeout(() => {
            reject(new Error('Connection timeout - server not responding'));
          }, 5000);
        });
  
        // 5. Navigate to chat
        history.push(`/chat/${data.responsePayload.id}`);
  
      } catch (error) {
        console.error('Login error:', error);
        alert(`Login failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
      }
    };

    return ( 
        <div className="login-container">
            <form onSubmit={handleLogin} className="login-form">
                <h2>Login</h2>
                <input
                    type="email"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
                <button type="submit">Login</button>
            </form>
        </div>
    )
};

export default Login;
