import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { socketService } from '../../services/socket'; // Assuming this is the socket service you have set up
import './ChatWindow.css';

const ChatWindow: React.FC = () => {
  const { userId } = useParams<{ userId: string }>();
  const [messages, setMessages] = useState<string[]>([]);
  const [newMessage, setNewMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const { receiverId, senderId } = useParams<{ receiverId: string; senderId: string }>();

  useEffect(() => {
    const initializeSocket = async () => {
      const token = localStorage.getItem('token');
      if (token && senderId) {
        try {
          await socketService.initializeSocket(token, senderId);
        } catch (error) {
          console.error('Error initializing socket:', error);
        }
      }
    };

    initializeSocket();

    return () => {
      console.log('Cleaning up WebSocket connection...');
      const socket = socketService.getSocket();
      if (socket) {
        socket.off('message');
      }
      socketService.disconnect();
    };
  }, []);


  useEffect(() => {
    // Listen for incoming messages
    socketService.getSocket()?.on('message', (message: string) => {
      setMessages((prevMessages) => [...prevMessages, message]);
    });

    return () => {
      // Clean up WebSocket connection on component unmount
      socketService.disconnect();
    };
  }, [userId]);

  useEffect(() => {
    const socket = socketService.getSocket();
    if (socket) {
      socket.on('new_message', (message: any) => {
        console.log('New message received:', message);
        setMessages((prevMessages) => [...prevMessages, `${message.senderId}: ${message.content}`]);
      });
    }

    return () => {
      if (socket) {
        socket.off('new_message');
      }
    };
  }, []);

  const sendMessage = (e: React.FormEvent) => {
    e.preventDefault();

    if (!newMessage.trim()) return;

    if (!receiverId || !senderId) {
      console.error('Missing sender or receiver ID');
      return;
    }



    socketService.getSocket()?.emit('send_message', {
      receiverId,
      senderId,
      message: newMessage,
    });

    setMessages((prevMessages) => [...prevMessages, `You: ${newMessage}`]);
    setNewMessage('');
  };

  return (
    <div className="chat-window">
      <div className="chat-header">
        <h2>Chat with User {userId}</h2>
      </div>
      <div className="messages">
        {messages.length === 0 ? (
          <p>No messages yet</p>
        ) : (
          messages.map((message, index) => (
            <div key={index} className={`message ${message.startsWith('You') ? 'sent' : 'received'}`}>
              <p>{message}</p>
            </div>
          ))
        )}
      </div>

      <form onSubmit={sendMessage} className="send-message-form">
        <input
          type="text"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          placeholder="Type a message..."
          disabled={isLoading}
        />
        <button type="submit" disabled={isLoading} className="send-button">
          {isLoading ? 'Sending...' : 'Send'}
        </button>
      </form>
    </div>
  );
};

export default ChatWindow;
