import { io, Socket } from 'socket.io-client';

class SocketService {
  private static instance: SocketService;
  socket: Socket | null = null;

  public static getInstance(): SocketService {
    if (!SocketService.instance) {
      SocketService.instance = new SocketService();
    }
    return SocketService.instance;
  }

  initializeSocket(token: string): Promise<Socket> {
    return new Promise((resolve, reject) => {
      if (this.socket?.connected) {
        console.log("✅ Reusing existing WebSocket connection");
        return resolve(this.socket);
      }

      const cleanToken = token.replace('Bearer ', '');
      const userId = localStorage.getItem('userId');

      this.socket = io('http://localhost:8080', {
        path: '/socket.io',
        auth: { token: cleanToken },
        transports: ['websocket'],
        reconnectionAttempts: 5, // Retry up to 5 times
        reconnectionDelay: 2000, // Wait 2 seconds between retries
        query: { userId, token: cleanToken },
        withCredentials: true,
      });

      this.socket.on('connect', () => {
        console.log("✅ WebSocket connected");
        resolve(this.socket!);
      });

      this.socket.on('connect_error', (err) => {
        console.error('❌ Connection error:', err.message);
        reject(err);
      });

      this.socket.on('disconnect', () => {
        console.log("🔌 WebSocket disconnected");
      });
    });
  }

  onConnectionStatusChange(callback: (status: 'connected' | 'disconnected' | 'error') => void) {
    if (!this.socket) return;

    this.socket.on('connect', () => callback('connected'));
    this.socket.on('disconnect', () => callback('disconnected'));
    this.socket.on('connect_error', () => callback('error'));
  }

  disconnect() {
    if (this.socket) {
      console.log("🔌 Disconnecting WebSocket...");
      this.socket.disconnect();
      this.socket = null;
    }
  }
}

export const socketService = SocketService.getInstance();
