import React, { useState, useEffect } from 'react';
import './ChatWindow.css';
import MessageList from './MessageList';
import MessageInput from './MessageInput';
import UploadedFiles from './UploadedFiles';
import { FaPaperclip, FaUser, FaCamera } from 'react-icons/fa';
import io, { Socket } from 'socket.io-client';

const SOCKET_SERVER_URL = 'http://localhost:8080'; // Adjust port as needed

const ChatWindow: React.FC = () => {
    const [uploadedFiles, setUploadedFiles] = useState<File[]>([]);
    const [socket, setSocket] = useState<Socket | null>(null);
    const [messages, setMessages] = useState<any[]>([]);

    useEffect(() => {
        // Connect to socket server
        const newSocket = io(SOCKET_SERVER_URL);
        setSocket(newSocket);

        // Listen for incoming messages
        newSocket.on('message', (message) => {
            setMessages((prevMessages) => [...prevMessages, message]);
        });

        // Listen for user-joined event
        newSocket.on('user-joined', (username) => {
            setMessages((prevMessages) => [
                ...prevMessages,
                { system: true, text: `${username} has joined the chat.` }
            ]);
        });

        // Cleanup on unmount
        return () => {
            newSocket.close();
        };
    }, []);

    const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
        if (event.target.files && event.target.files[0]) {
            const file = event.target.files[0];
            setUploadedFiles((prevFiles) => [...prevFiles, file]);
            // Emit file upload event to server
            if (socket) {
                const reader = new FileReader();
                reader.onload = (e) => {
                    socket.emit('file-upload', {
                        name: file.name,
                        type: file.type,
                        data: e.target?.result
                    });
                };
                reader.readAsDataURL(file);
            }
        }
    };

    const handleSendMessage = (message: string) => {
        if (socket) {
            socket.emit('message', {
                text: message,
                userId: 'user123',
                timestamp: new Date().toISOString()
            });
        }
    };

    return (
        <div className="chat-container">
            <div className="chat-header">
                <h1>Chat Room</h1>
            </div>
            <div className="chat-content">
                <MessageList messages={messages} currentUserId="user123" />
                <UploadedFiles files={uploadedFiles} />
                <div className="chat-input-area">
                    <label htmlFor="file-upload" className="file-upload-icon">
                        <FaPaperclip />
                    </label>
                    <input
                        id="file-upload"
                        type="file"
                        style={{ display: 'none' }}
                        onChange={handleFileUpload}
                    />
                    <MessageInput onSend={handleSendMessage} />
                </div>
            </div>
        </div>
    );
};

export default ChatWindow;
