import React, { useState, useEffect, useCallback } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import './ChatWindow.css';
import MessageList from './MessageList';
import MessageInput from './MessageInput';
import UploadedFiles from './UploadedFiles';
import { FaPaperclip } from 'react-icons/fa';
import io, { Socket, ManagerOptions, SocketOptions } from 'socket.io-client';


const SOCKET_SERVER_URL = 'http://localhost:8080/chat';


interface ChatParams {
  userId: string;
  receiverId?: string;
}

const ChatWindow: React.FC = () => {
  const { userId, receiverId } = useParams<ChatParams>();
  const history = useHistory();
  const [uploadedFiles, setUploadedFiles] = useState<File[]>([]);
  const [socket, setSocket] = useState<Socket | null>(null);
  const [messages, setMessages] = useState<any[]>([]);
  const [clientId, setClientId] = useState<string | null>(null);
  const [connectionStatus, setConnectionStatus] = useState<'connecting' | 'connected' | 'disconnected'>('connecting');

  // Initialize socket connection
  const initSocket = useCallback(() => {
    const token = localStorage.getItem('token');
    const storedUserId = localStorage.getItem('userId');

    if (!token || !storedUserId || storedUserId !== userId) {
      history.replace('/login');
      return null;
    }

    // Reuse existing socket if available
    if (window.socket && window.socket.connected) {
      setSocket(window.socket);
      setClientId(window.socket.id || null);
      return window.socket;
    }

    const socketOptions: Partial<ManagerOptions & SocketOptions> = {
      path: '/chat/socket.io',
      auth: { token: `Bearer ${token}` },
      query: { userId },
      transports: ['websocket'],
      reconnectionAttempts: 5,
      reconnectionDelay: 1000,
    };

    const newSocket = io(SOCKET_SERVER_URL, socketOptions);

    newSocket.on('connect', () => {
      console.log('Connected with ID:', newSocket.id);
      setClientId(newSocket.id || null);
      setConnectionStatus('connected');

      if (receiverId) {
        newSocket.emit('join_conversation', { userId, receiverId });
      }
    });

    newSocket.on('disconnect', () => {
      setConnectionStatus('disconnected');
    });

    newSocket.on('connect_error', (err) => {
      console.error('Connection error:', err);
      if (err.message.includes('auth')) {
        localStorage.clear();
        history.push('/login');
      }
    });

    newSocket.on('new_message', (message) => {
      setMessages(prev => [...prev, message]);
    });

    newSocket.on('file-sent', (fileMessage) => {
      setMessages(prev => [...prev, fileMessage]);
    });

    // Store socket globally
    window.socket = newSocket;
    setSocket(newSocket);

    return newSocket;
  }, [userId, receiverId, history]);

  useEffect(() => {
    const newSocket = initSocket();

    return () => {
      // Clean up listeners but keep the connection alive
      newSocket?.off('connect');
      newSocket?.off('disconnect');
      newSocket?.off('connect_error');
      newSocket?.off('new_message');
      newSocket?.off('file-sent');
    };
  }, [initSocket]);

  const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    if (event.target.files?.[0] && socket) {
      const file = event.target.files[0];
      setUploadedFiles(prev => [...prev, file]);

      const formData = new FormData();
      formData.append('file', file);
      formData.append('senderId', userId);
      if (receiverId) {
        formData.append('receiverId', receiverId);
      }

      socket.emit('fileUpload', formData, (response: any) => {
        if (response.status !== 'success') {
          console.error('File upload failed:', response.message);
        }
      });
    }
  };

  const handleSendMessage = (message: string) => {
    if (socket) {
      const messageData = receiverId 
        ? { senderId: userId, receiverId, content: message }
        : { senderId: userId, content: message };

      socket.emit('send_message', messageData, (response: any) => {
        if (response.status !== 'success') {
          console.error('Message sending failed:', response.message);
        }
      });
    }
  };

  return (
    <div className="chat-container">
      <div className="chat-header">
        <h1>Chat Room - User: {localStorage.getItem('username')}</h1>
        {clientId && (
          <div>
            <p>Connection ID: {clientId}</p>
            <p>Status: {connectionStatus}</p>
          </div>
        )}
      </div>
      <div className="chat-content">
        <MessageList messages={messages} currentUserId={userId} />
        <UploadedFiles files={uploadedFiles} />
        <div className="chat-input-area">
          <label htmlFor="file-upload" className="file-upload-icon">
            <FaPaperclip />
          </label>
          <input
            id="file-upload"
            type="file"
            style={{ display: 'none' }}
            onChange={handleFileUpload}
          />
          <MessageInput onSend={handleSendMessage} />
        </div>
      </div>
    </div>
  );
};

export default ChatWindow;
