import React from 'react';
import './ChatWindow.css';
import MessageList from './MessageList';
import MessageInput from './MessageInput';
import FileUpload from './FileUpload';

const ChatWindow: React.FC = () => {
    return (
        <div className="chat-container">
            <div className="chat-header">
                <h1>Chat Room</h1>
            </div>
            <div className="chat-content">
                <p>Welcome to the chat! Start messaging below.</p>
                <MessageList messages={[]} currentUserId="user123" />
                <FileUpload onFileUpload={(file: File) => console.log('File uploaded:', file)} />
                <MessageInput onSend={(message: string) => console.log('Message sent:', message)} />
            </div>
        </div>
    );
};

export default ChatWindow;
