import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import { loginResponse } from 'src/types';
import './Login.css';

const Login: React.FC = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const history = useHistory();

   // In your Login component
const handleLogin = async (e: React.FormEvent) => {
  e.preventDefault();
  try {
    const response = await fetch('http://localhost:8080/api/v1/auth/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ email, password }),
    });

    if (!response.ok) throw new Error('Login failed');

    const data = await response.json();
    
    // Store auth data
    localStorage.setItem('token', data.accessToken);
    localStorage.setItem('userId', data.responsePayload.id);
    localStorage.setItem('username', data.responsePayload.fullname);

    // Initialize socket connection before navigation
    const socket = io(SOCKET_SERVER_URL, {
      path: '/chat/socket.io',
      auth: { token: `Bearer ${data.accessToken}` },
      query: { userId: data.responsePayload.id },
      transports: ['websocket'],
    });

    // Wait for connection before navigating
    await new Promise<void>((resolve) => {
      socket.on('connect', () => {
        console.log('Socket connected, navigating to chat');
        resolve();
      });
      
      socket.on('connect_error', (err) => {
        console.error('Connection error:', err);
        throw new Error('Socket connection failed');
      });
    });

    // Navigate after successful connection
    history.push(`/chat/${data.responsePayload.id}`);
    
  } catch (error) {
    console.error('Login error:', error);
    alert(`Login failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
  }
};

    return (
        <div className="login-container">
            <form onSubmit={handleLogin} className="login-form">
                <h2>Login</h2>
                <input
                    type="email"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
                <button type="submit">Login</button>
            </form>
        </div>
    );
};

export default Login;
