import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import { loginResponse } from 'src/types';
import './Login.css';
import { io } from 'socket.io-client';


const Login: React.FC = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const history = useHistory();
  
    const handleLogin = async (e: React.FormEvent) => {
      e.preventDefault();
      try {
        // 1. First perform the login request
        const response = await fetch('http://localhost:8080/api/v1/auth/login', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ email, password }),
        });
  
        if (!response.ok) {
          throw new Error('Login failed');
        }
  
        const data: loginResponse = await response.json();
        
        // 2. Store the auth data
        localStorage.setItem('token', data.accessToken);
        localStorage.setItem('userId', data.responsePayload.id);
        localStorage.setItem('username', data.responsePayload.fullname);
  
        // 3. Initialize Socket.IO connection
        const socket = io('http://localhost:8080/chat', { // Use your server URL
          path: '/chat/socket.io',
          auth: {
            token: `Bearer ${data.accessToken}`
          },
          transports: ['websocket']
        });
  
        // 4. Wait for connection before navigating
        await new Promise<void>((resolve, reject) => {
          socket.on('connect', () => {
            console.log('Socket connected!');
            resolve();
          });
  
          socket.on('connect_error', (err:any) => {
            console.error('Connection error:', err);
            reject(new Error('Socket connection failed'));
          });
  
          // Timeout after 5 seconds
          setTimeout(() => {
            reject(new Error('Connection timeout'));
          }, 5000);
        });
  
        // 5. Navigate to chat
        history.push(`/chat/${data.responsePayload.id}`);
  
      } catch (error) {
        console.error('Login error:', error);
        alert(`Login failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
      }
    };

    return ( 
        <div className="login-container">
            <form onSubmit={handleLogin} className="login-form">
                <h2>Login</h2>
                <input
                    type="email"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
                <button type="submit">Login</button>
            </form>
        </div>
    )
};

export default Login;
