import { io, Socket } from 'socket.io-client';

class SocketService {
    static #instance: SocketService;
    #socket: Socket | null = null;

    constructor() {
        if (SocketService.#instance) {
            return SocketService.#instance;
        }
        SocketService.#instance = this;
    }

    static getInstance(): SocketService {
        if (!SocketService.#instance) {
            SocketService.#instance = new SocketService();
        }
        return SocketService.#instance;
    }

    initializeSocket(token: string): Promise<Socket> {
        return new Promise((resolve, reject) => {
            if (this.#socket?.connected) {
                resolve(this.#socket);
                return;
            }

            this.#socket = io('http://localhost:8080/chat', {
                path: '/socket.io',
                auth: {
                    token: `Bearer ${token}`
                },
                transports: ['websocket'],
                reconnectionAttempts: 5,
                reconnectionDelay: 1000,
                withCredentials: true
            });

            this.#socket.on('connect', () => {
                console.log('Socket connected with ID:', this.#socket?.id);
                resolve(this.#socket!);
            });

            this.#socket.on('connect_error', (error) => {
                console.error('Socket connection error:', error);
                reject(error);
            });

            this.#socket.on('disconnect', (reason) => {
                console.log('Socket disconnected:', reason);
            });
        });
    }

    getSocket(): Socket | null {
        return this.#socket;
    }

    disconnect(): void {
        if (this.#socket) {
            this.#socket.disconnect();
            this.#socket = null;
        }
    }
}

export const socketService = SocketService.getInstance();

export const sendMessage = (message: { text: string; userId: string }) => {
    if (socketService.getSocket()) {
        socketService.getSocket()?.emit('message', message);
    } else {
        console.error('Socket is not connected');
    }
};

export const onMessageReceived = (callback: (message: any) => void) => {
    if (socketService.getSocket()) {
        socketService.getSocket()?.on('message', callback);
    }
};

export const uploadFile = (fileData: any) => {
    if (socketService.getSocket()) {
        socketService.getSocket()?.emit('fileUpload', fileData);
    }
};

export const onFileUploaded = (callback: (response: any) => void) => {
    if (socketService.getSocket()) {
        socketService.getSocket()?.on('fileUploaded', callback);
    }
};

export const joinRoom = (roomId: string) => {
    if (socketService.getSocket()) {
        socketService.getSocket()?.emit('joinRoom', { roomId });
    }
};

export const leaveRoom = (roomId: string) => {
    if (socketService.getSocket()) {
        socketService.getSocket()?.emit('leaveRoom', { roomId });
    }
};

export const onUserJoined = (callback: (data: any) => void) => {
    if (socketService.getSocket()) {
        socketService.getSocket()?.on('user-joined', callback);
    }
};

export const onUserLeft = (callback: (data: any) => void) => {
    if (socketService.getSocket()) {
        socketService.getSocket()?.on('user-left', callback);
    }
};

export const acknowledgeMessage = (messageId: string) => {
    if (socketService.getSocket()) {
        socketService.getSocket()?.emit('acknowledge', { messageId });
    }
};

export const onAcknowledged = (callback: (data: any) => void) => {
    if (socketService.getSocket()) {
        socketService.getSocket()?.on('acknowledged', callback);
    }
};

export const sendTyping = (typingData: { senderId: string; receiverId: string; chatId: string }) => {
    if (socketService.getSocket()) {
        socketService.getSocket()?.emit('typing', typingData);
    }
};

export const stopTyping = (typingData: { senderId: string; receiverId: string }) => {
    if (socketService.getSocket()) {
        socketService.getSocket()?.emit('stop_typing', typingData);
    }
};

export const onTyping = (callback: (data: any) => void) => {
    if (socketService.getSocket()) {
        socketService.getSocket()?.on('typing', callback);
    }
};

export const onStopTyping = (callback: (data: any) => void) => {
    if (socketService.getSocket()) {
        socketService.getSocket()?.on('stop_typing', callback);
    }
};

export const sendReadReceipt = (readReceiptData: { messageId: string; receiverId: string }) => {
    if (socketService.getSocket()) {
        socketService.getSocket()?.emit('read_receipt', readReceiptData);
    }
};

export const onMessageRead = (callback: (data: any) => void) => {
    if (socketService.getSocket()) {
        socketService.getSocket()?.on('message_read', callback);
    }
};
