import React, { useState } from 'react';
import { useN } from 'react-router-dom';
import { loginResponse } from 'src/types';
import './Login.css';
import { socketService } from '../../services/socket';

const Login: React.FC = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState<string>('');
    const [isLoading, setIsLoading] = useState(false);
    const history = useHistory();
  
    const handleLogin = async (e: React.FormEvent) => {
      e.preventDefault();
      setError('');
      setIsLoading(true);
      
      try {
        // 1. First perform the login request
        const response = await fetch('http://localhost:8080/api/v1/auth/login', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ email, password }),
          credentials: 'include'  // Important: include credentials
        });
  
        if (!response.ok) {
          throw new Error('Invalid credentials');
        }
  
        const data: loginResponse = await response.json();
        
        console.log('Login successful, token received:', {
          tokenExists: !!data.accessToken,
          tokenLength: data.accessToken?.length,
          userId: data.responsePayload.id,
          fullname: data.responsePayload.fullname
        });
        
        // 2. Store the auth data
        localStorage.setItem('token', data.accessToken);
        localStorage.setItem('userId', data.responsePayload.id);
        localStorage.setItem('username', data.responsePayload.fullname);
        
        // Set token as cookie as well
        document.cookie = `accessToken=${data.accessToken}; path=/`;
  
        // 3. Initialize Socket connection with delay to ensure cookie is set
        try {
          console.log('Attempting socket connection...');
          // Small delay to ensure cookie is set
          await new Promise(resolve => setTimeout(resolve, 100));
          await socketService.initializeSocket(data.accessToken);
          console.log('Socket connection successful');
          // 4. Navigate to chat on successful connection
          history.push(`/chat/${data.responsePayload.id}`);
        } catch (error) {
          console.error('Socket connection error:', error);
          setError('Failed to establish chat connection. Please try again.');
          // Clear stored data on socket connection failure
          localStorage.removeItem('token');
          localStorage.removeItem('userId');
          localStorage.removeItem('username');
          document.cookie = 'accessToken=; expires=Thu, 01 Jan 1970 00:00:00 GMT; path=/';
          // Disconnect socket if it exists
          socketService.disconnect();
        }
  
      } catch (error) {
        console.error('Login error:', error);
        setError(error instanceof Error ? error.message : 'An unknown error occurred');
      } finally {
        setIsLoading(false);
      }
    };

    return ( 
        <div className="login-container">
            <form onSubmit={handleLogin} className="login-form">
                <h2>Login</h2>
                {error && <div className="error-message">{error}</div>}
                <input
                    type="email"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    disabled={isLoading}
                    required
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    disabled={isLoading}
                    required
                />
                <button type="submit" disabled={isLoading}>
                    {isLoading ? 'Logging in...' : 'Login'}
                </button>
            </form>
        </div>
    );
};

export default Login;
