import React, { useState, useEffect } from 'react';
import './ChatWindow.css';
import MessageList from './MessageList';
import MessageInput from './MessageInput';
import UploadedFiles from './UploadedFiles';
import { FaPaperclip } from 'react-icons/fa';
import io, { Socket } from 'socket.io-client';

const SOCKET_SERVER_URL = 'http://localhost:8080/chat'; // Adjust port as needed

const ChatWindow: React.FC = () => {
    const [uploadedFiles, setUploadedFiles] = useState<File[]>([]);
    const [socket, setSocket] = useState<Socket | null>(null);
    const [messages, setMessages] = useState<any[]>([]);
    const [clientId, setClientId] = useState<string | null>(null); // New state for client ID

    useEffect(() => {
        const newSocket = io(SOCKET_SERVER_URL);
        setSocket(newSocket);

        newSocket.on('connect', () => {
            console.log('Connected to server:', newSocket.id);
            setClientId(newSocket.id ?? null); // Use null if newSocket.id is undefined
        });

        // Listen for incoming messages
        newSocket.on('message', (message) => {
            setMessages((prevMessages) => [...prevMessages, message]);
        });

        // Listen for user-joined event
        newSocket.on('user-joined', (username) => {
            setMessages((prevMessages) => [
                ...prevMessages,
                { system: true, text: `${username} has joined the chat.` },
            ]);
        });

        // Listen for file-sent event
        newSocket.on('file-sent', (fileMessage) => {
            setMessages((prevMessages) => [...prevMessages, fileMessage]);
        });

        // Handle connection errors
        newSocket.on('connect_error', (error) => {
            console.error('Connection error:', error);
        });

        // Cleanup on unmount
        return () => {
            newSocket.close();
        };
    }, []);

    const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
        if (event.target.files && event.target.files[0]) {
            const file = event.target.files[0];
            setUploadedFiles((prevFiles) => [...prevFiles, file]);

            if (socket) {
                const reader = new FileReader();
                reader.onload = (e) => {
                    socket.emit('file-upload', {
                        name: file.name,
                        type: file.type,
                        data: e.target?.result,
                    });
                };
                reader.readAsDataURL(file);
            }
        }
    };

    const handleSendMessage = (message: string) => {
        if (socket) {
            socket.emit('message', {
                text: message,
                userId: 'user123',
                timestamp: new Date().toISOString(),
            });
        }
    };

    return (
        <div className="chat-container">
            <div className="chat-header">
                <h1>Chat Room</h1>
                {clientId && <p>Client ID: {clientId}</p>} {/* Display client ID */}
            </div>
            <div className="chat-content">
                <MessageList messages={messages} currentUserId="user123" />
                <UploadedFiles files={uploadedFiles} />
                <div className="chat-input-area">
                    <label htmlFor="file-upload" className="file-upload-icon">
                        <span>
                            <FaPaperclip />
                        </span>
                    </label>
                    <input
                        id="file-upload"
                        type="file"
                        style={{ display: 'none' }}
                        onChange={handleFileUpload}
                    />
                    <MessageInput onSend={handleSendMessage} />
                </div>
            </div>
        </div>
    );
};

export default ChatWindow;
