import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import { loginResponse } from 'src/types';
import './Login.css';
import { socketService } from '../../services/socket';

const Login: React.FC = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const history = useHistory();
  
    const handleLogin = async (e: React.FormEvent) => {
      e.preventDefault();
      try {
        // 1. First perform the login request
        const response = await fetch('http://localhost:8080/api/v1/auth/login', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ email, password }),
        });
  
        if (!response.ok) {
          throw new Error('Login failed');
        }
  
        const data: loginResponse = await response.json();
        
        console.log('Login successful, token received:', {
          tokenExists: !!data.accessToken,
          tokenLength: data.accessToken?.length,
          userId: data.responsePayload.id,
          fullname: data.responsePayload.fullname
        });
        
        // 2. Store the auth data
        localStorage.setItem('token', data.accessToken);
        localStorage.setItem('userId', data.responsePayload.id);
        localStorage.setItem('username', data.responsePayload.fullname);
  
        // 3. Initialize Socket connection
        try {
          await socketService.initializeSocket(data.accessToken);
          // 4. Navigate to chat on successful connection
          history.push(`/chat/${data.responsePayload.id}`);
        } catch (socketError: any) {
          console.error('Socket connection error:', socketError);
          throw new Error(`Socket connection failed: ${socketError?.message || 'Unknown error'}`);
        }
  
      } catch (error) {
        console.error('Login error:', error);
        alert(`Login failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
      }
    };

    return ( 
        <div className="login-container">
            <form onSubmit={handleLogin} className="login-form">
                <h2>Login</h2>
                <input
                    type="email"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
                <button type="submit">Login</button>
            </form>
        </div>
    )
};

export default Login;
