import React, { useState, useEffect } from 'react';
'../../services/socket'

const ChatWindow = () => {
  const [connectionStatus, setConnectionStatus] = useState<'connected' | 'connecting' | 'disconnected'>('disconnected');

  useEffect(() => {
    // Register connection status change handlers
    const handleConnectionStatusChange = (status: 'connected' | 'connecting' | 'disconnected' | 'error') => {
      console.log('Connection status:', status);
      setConnectionStatus(status);
    };

    socketService.onConnect(handleConnectionStatusChange);

    // When the component mounts, try connecting the socket
    if (!socketService.socket?.connected) {
      setConnectionStatus('connecting');
      socketService.connectSocketWithRetry(() => setConnectionStatus('connected'));
    }

    // Cleanup function to handle socket disconnection
    return () => {
      console.log('Cleaning up WebSocket connection...');
      socketService.disconnect();
      setConnectionStatus('disconnected');
    };
  }, []);

  return (
    <div>
      <h1>Chat Window</h1>
      <p>Connection Status: {connectionStatus}</p>
      {/* Render your chat UI here */}
    </div>
  );
};

export default ChatWindow;
