import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { socketService } from '../../services/socket';  
import './ChatWindow.css';
import MessageList from './MessageList';

interface Message {
  senderId: string;
  content: string;
}

const ChatWindow: React.FC = () => {
  const { userId } = useParams<{ userId: string }>();
  const [messages, setMessages] = useState<Message[]>([]);
  const [newMessage, setNewMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const { receiverId, senderId } = useParams<{ receiverId: string; senderId: string }>();

  useEffect(() => {
    const initializeSocket = async () => {
      const token = localStorage.getItem('token');
      if (token && senderId) {
        try {
          console.log('Initializing WebSocket with token and senderId:', token, senderId);
          await socketService.initializeSocket(token, senderId);
          console.log('WebSocket initialized successfully');
        } catch (error) {
          console.error('Error initializing WebSocket:', error);
        }
      } else {
        console.error('Token or senderId is missing');
      }
    };

    initializeSocket();

    return () => {
      console.log('Cleaning up WebSocket connection...');
      const socket = socketService.getSocket();
      if (socket) {
        socket.off('message');
      }
      socketService.disconnect();
    };
  }, []);

  useEffect(() => {
    // Listen for incoming messages
    socketService.getSocket()?.on('message', (message: string) => {
      setMessages((prevMessages) => [...prevMessages, message]);
    });

    return () => {
      // Clean up WebSocket connection on component unmount
      socketService.disconnect();
    };
  }, [userId]);

  useEffect(() => {
    const socket = socketService.getSocket();
    if (socket) {
      socket.on('new_message', (message: any) => {
        console.log('New message received:', message);
        setMessages((prevMessages) => [...prevMessages, { senderId: message.senderId, content: message.content }]);
      });
    }

    return () => {
      if (socket) {
        console.log('Cleaning up new_message listener');
        socket.off('new_message');
      }
    };
  }, []);

  const sendMessage = (e: React.FormEvent) => {
    e.preventDefault();

    if (!newMessage.trim()) {
      console.error('Message content is empty');
      return;
    }

    if (!receiverId || !senderId) {
      console.error('Missing sender or receiver ID');
      return;
    }

    const payload = {
      receiverId,
      senderId,
      content: newMessage,
    };

    console.log('Sending message payload:', payload);

    const socket = socketService.getSocket();
    if (!socket || !socket.connected) {
      console.error('WebSocket is not connected');
      return;
    }

    socket.emit('send_message', payload, (response: any) => {
      if (response.status === 'success') {
        console.log('Message sent successfully:', response.data);
      } else {
        console.error('Failed to send message:', response.message);
      }
    });

    // Temporarily show the message in the UI
    setMessages((prevMessages) => [...prevMessages, { senderId: 'You', content: newMessage }]);
    setNewMessage('');
  };

  return (
    <div className="chat-window">
      <div className="chat-header">
        <h2>Chat with User {userId}</h2>
      </div>
      <div className="messages">
        {messages.length === 0 ? (
          <p>No messages yet</p>
        ) : (
          messages.map((message, index) => (
            <div key={index} className={`message ${message.senderId === 'You' ? 'sent' : 'received'}`}>
              <p>{message.senderId}: {message.content}</p>
            </div>
          ))
        )}
      </div>

      <form onSubmit={sendMessage} className="send-message-form">
        <input
          type="text"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          placeholder="Type a message..."
          disabled={isLoading}
        />
        <button type="submit" disabled={isLoading} className="send-button">
          {isLoading ? 'Sending...' : 'Send'}
        </button>
      </form>
    </div>
  );
};

export default ChatWindow;
