import React, { useState, useEffect } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import './ChatWindow.css';
import MessageList from './MessageList';
import MessageInput from './MessageInput';
import UploadedFiles from './UploadedFiles';
import { FaPaperclip } from 'react-icons/fa';
import io, { Socket } from 'socket.io-client';

const SOCKET_SERVER_URL = 'http://localhost:8080/chat';

interface ChatParams {
    userId: string;
    receiverId?: string; 
}

const ChatWindow: React.FC = () => {
    const { userId, receiverId } = useParams<ChatParams>();
    const history = useHistory();
    const [uploadedFiles, setUploadedFiles] = useState<File[]>([]);
    const [socket, setSocket] = useState<Socket | null>(null);
    const [messages, setMessages] = useState<any[]>([]);
    const [clientId, setClientId] = useState<string | null>(null);

    // Verify authentication on component mount
    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            history.push('/login');
            return;
        }

        const newSocket = io(SOCKET_SERVER_URL, {
            query: { 
                userId,
                token // Send token for authentication
            },
            auth: {
                token: `Bearer ${token}`
            }
        });

        setSocket(newSocket);

        newSocket.on('connect', () => {
            console.log('Connected to server:', newSocket.id);
            setClientId(newSocket.id ?? null);
        });

        // Listen for incoming messages
        newSocket.on('new_message', (message) => {
            setMessages((prevMessages) => [...prevMessages, message]);
        });

        // Listen for file-sent event
        newSocket.on('file-sent', (fileMessage) => {
            setMessages((prevMessages) => [...prevMessages, fileMessage]);
        });
        newSocket.on('typing', (typing) => {
            setMessages((prevMessages) => [...prevMessages, typing]);
        });
        newSocket.on('stop-tying', (fileMessage) => {
            setMessages((prevMessages) => [...prevMessages, fileMessage]);
        });

        // Handle connection errors
        newSocket.on('connect_error', (error) => {
            console.error('Connection error:', error);
            if (error.message === 'Authentication error') {
                history.push('/login');
            }
        });

        // Cleanup on unmount
        return () => {
            newSocket.close();
        };
    }, [userId, history]);

    const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
        if (event.target.files && event.target.files[0] && socket) {
            const file = event.target.files[0];
            setUploadedFiles((prevFiles) => [...prevFiles, file]);

            const formData = new FormData();
            formData.append('file', file);
            formData.append('senderId', userId);
            if (receiverId) {
                formData.append('receiverId', receiverId);
            }

            socket.emit('fileUpload', formData, (response: any) => {
                if (response.status === 'success') {
                    console.log('File uploaded successfully:', response.data);
                } else {
                    console.error('File upload failed:', response.message);
                }
            });
        }
    };

    const handleSendMessage = (message: string) => {
        if (socket) {
            const messageData = receiverId 
                ? { senderId: userId, receiverId, content: message }
                : { senderId: userId, content: message }; // For group chats

            socket.emit('send_message', messageData, (response: any) => {
                if (response.status === 'success') {
                    console.log('Message sent successfully:', response.data);
                } else {
                    console.error('Message sending failed:', response.message);
                }
            });
        }
    };

    return (
        <div className="chat-container">
            <div className="chat-header">
                <h1>Chat Room - User: {localStorage.getItem('username')}</h1>
                {clientId && <p>Connection ID: {clientId}</p>}
            </div>
            <div className="chat-content">
                <MessageList messages={messages} currentUserId={userId} />
                <UploadedFiles files={uploadedFiles} />
                <div className="chat-input-area">
                    <label htmlFor="file-upload" className="file-upload-icon">
                        <FaPaperclip />
                    </label>
                    <input
                        id="file-upload"
                        type="file"
                        style={{ display: 'none' }}
                        onChange={handleFileUpload}
                    />
                    <MessageInput onSend={handleSendMessage} />
                </div>
            </div>
        </div>
    );
};

export default ChatWindow;
