import { useParams, useNavigate } from 'react-router-dom';
import './ChatWindow.css';
import MessageList from './MessageList';
import MessageInput from './MessageInput';
import UploadedFiles from './UploadedFiles';
import { FaPaperclip, FaSpinner } from 'react-icons/fa';
import { socketService } from '../../services/socket';

interface ChatParams {
  userId: string;
  receiverId?: string;
  [key: string]: string | undefined;
}

interface Message {
  id: string;
  senderId: string;
  receiverId: string | undefined;
  content: string;
  timestamp: Date;
  status?: 'delivered' | 'read';
  chatId: string;
}

const ChatWindow: React.FC = () => {
  const { userId, receiverId } = useParams<ChatParams>();
  const navigate = useNavigate();
  const [uploadedFiles, setUploadedFiles] = useState<File[]>([]);
  const [messages, setMessages] = useState<Message[]>([]);
  const [isTyping, setIsTyping] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState<'connecting' | 'connected' | 'disconnected' | 'error'>('connecting');
  const [isUploading, setIsUploading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const connectSocket = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token || !userId) {
        console.error('No token or userId found');
        setError('No token or userId found');
        navigate('/login');
        return;
      }

      setConnectionStatus('connecting');

      const socket = await socketService.initializeSocket(token);

      // Listen for connection status changes
      socketService.onConnectionStatusChange(setConnectionStatus);

      const onNewMessage = (message: Message) => {
        setMessages(prev => [...prev, message]);
      };

      const onFileSent = (fileMessage: Message) => {
        setMessages(prev => [...prev, fileMessage]);
        setIsUploading(false);
      };

      const onError = (err: Error) => {
        console.error('Socket error:', err);
        setError(err.message);
        setConnectionStatus('disconnected');
      };

      // Add other event listeners
      socket.on('new_message', onNewMessage);
      socket.on('file-sent', onFileSent);
      socket.on('error', onError);
      socket.on('connect_error', (err: Error) => {
        console.error('Connection error:', err);
        setConnectionStatus('disconnected');
      });
      // Load initial messages
      if (receiverId) {
        socket.emit('get_messages', { userId, receiverId }, (response: any) => {
          if (response.status === 'success') {
            setMessages(response.data);
          }
        });
      }

      return () => {
        socket.off('new_message', onNewMessage);
        socket.off('file-sent', onFileSent);
        socket.off('error', onError);
      };

    } catch (error: any) {
      console.error('Socket connection error:', error);
      setConnectionStatus('disconnected');
      setError('Failed to connect to chat server');
      navigate('/login');
    }
  };

  const connectSocketWithRetry = async (retries = 3) => {
    while (retries > 0) {
      try {
        await connectSocket();
        return;
      } catch (error) {
        console.error('Retrying WebSocket connection...', error);
        retries--;
        await new Promise((resolve) => setTimeout(resolve, 1000));
      }
    }
    console.error('Failed to connect WebSocket after retries');
    setConnectionStatus('error');
  };

  useEffect(() => {
    connectSocketWithRetry();
    return () => socketService.disconnect();
  }, [userId, receiverId]);

  const handleFileUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    if (!event.target.files?.[0] || !socketService.socket || !userId) return;

    const file = event.target.files[0];
    setIsUploading(true);
    setError(null);

    try {
      const formData = new FormData();
      formData.append('file', file);
      formData.append('senderId', userId);
      if (receiverId) {
        formData.append('receiverId', receiverId);
      }

      socketService.socket.emit('fileUpload', formData, (response: any) => {
        if (response.status !== 'success') {
          throw new Error(response.message || 'File upload failed');
        }
      });

      setUploadedFiles(prev => [...prev, file]);
    } catch (error) {
      console.error('File upload error:', error);
      setError(error instanceof Error ? error.message : 'File upload failed');
      setIsUploading(false);
    }
  };

  const handleSendMessage = (message: string) => {
    if (!socketService.socket?.connected || !userId) {
      setError('Not connected to chat server');
      return;
    }

    setError(null);

    const messageData = receiverId 
      ? { senderId: userId, receiverId, content: message }
      : { senderId: userId, content: message };

    socketService.socket.emit('send_message', messageData, (response: any) => {
      if (response.status !== 'success') {
        setError(response.message || 'Failed to send message');
      }
    });
  };

  const renderConnectionStatus = () => {
    switch (connectionStatus) {
      case 'connecting':
        return <span className="connecting"><span className="spinner"></span> Connecting...</span>;
      case 'connected':
        return <span className="connected">Connected</span>;
      case 'disconnected':
        return <span className="disconnected">Disconnected</span>;
      default:
        return null;
    }
  };

  return (
    <div className="chat-container">
      <div className="chat-header">
        <h1>Chat Room - User: {localStorage.getItem('username')}</h1>
        <div className="connection-status">
          {renderConnectionStatus()}
        </div>
      </div>

      {error && <div className="error-message">{error}</div>}

      <div className="chat-content">
        {userId && (
          <>
            <MessageList 
              messages={messages} 
              currentUserId={userId} 
              receiverId={receiverId} 
            />

            <UploadedFiles 
              files={uploadedFiles} 
              isUploading={isUploading} 
            />

            <div className="chat-input-area">
              <label htmlFor="file-upload" className="file-upload-icon">

                <input
                  id="file-upload"
                  type="file"
                  style={{ display: 'none' }}
                  onChange={handleFileUpload}
                  disabled={isUploading}
                />
              </label>

              <MessageInput 
                onSend={handleSendMessage} 
                disabled={connectionStatus !== 'connected' || isUploading}
              />
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default ChatWindow;
