import React from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import ChatWindow from './components/Chat/ChatWindow';
import Header from './components/Layout/Header';
import Sidebar from './components/Layout/Sidebar';
import Register from './components/Auth/Register';
import Login from './components/Auth/Login';
import './App.css';
import './components/Layout/Header.css';
import './components/Layout/Sidebar.css';
import './components/Chat/ChatWindow.css';
import './components/Chat/MessageList.css';
import './components/Chat/MessageInput.css';
import './components/Chat/UploadedFiles.css';
import './components/Auth/Register.css';
import './components/Auth/Login.css';
import './components/Chat/Message.css';

const App: React.FC = () => {
    return (
        <Router>
            <div className="app">
                <Header />
                <h1 className="app-title">Suxch Chat App</h1>
                <div className="main-layout">
                    <Sidebar />
                    <Switch>
                        <Route exact path="/" component={ChatWindow} />
                        <Route path="/register" component={Register} />
                    </Switch>
                </div>
            </div>
        </Router>
    );
};

export default App;
